/* when the user clicks on the button, toggle between hiding and showing the dropdown content */
function myFunction (clicked_id) {
	var buttonID = document.getElementById(clicked_id);
	var divID = buttonID.nextElementSibling;
	divID.classList.toggle("show");
}


// close the drop down menu if the user clicks outside of it
window.onclick = function(event) {
	if (!event.target.matches('.dropbtn')) {
		var dropdowns = document.getElementById("dropdown-content");
		var i;
		for (i = 0; i < dropdowns.length; i++) {
			var openDropdown = dropdowns[i];
			if (openDropdown.classList.contains('show')) {
				openDropdown.classList.remove('show');
			}
		}
	}
}

var overlay = document.getElementById("overlay");

window.addEventListener('load', function() {
	overlay.style.display = 'none';
})
